<script>
    function validateForm () {
        event.preventDefault();
        var americanExpressCreditCardNo = /^(?:3[47][0-9]{13})$/;
        var visaCardNo = /^(?:4[0-9]{12}(?:[0-9]{3})?)$/;
        var masterCardNo = /^(?:5[1-5][0-9]{14})$/;
        var discoverCardNo = /^(?:6(?:011|5[0-9][0-9])[0-9]{12})$/;
        var dinersClubCardNo = /^(?:3(?:0[0-5]|[68][0-9])[0-9]{11})$/;
        var jcbCardNo = /^(?:(?:2131|1800|35\d{3})\d{11})$/;
        var date = new Date();
        var year = date.getFullYear();
        var month = date.getMonth();

        var expYear = $('#expYear').val();
        var expMonth = $('#expMonth').val();
        console.log(Number(expMonth));
        var cardNumber = $('#cardNumber').val();
        if((cardNumber.match(americanExpressCreditCardNo) ||
           cardNumber.match(visaCardNo)                  ||
           cardNumber.match(masterCardNo)                ||
           cardNumber.match(discoverCardNo)              ||
           cardNumber.match(dinersClubCardNo)            ||
           cardNumber.match(jcbCardNo))) {
           if(Number(expMonth) >= month+1) {
                $('#creditForm').get(0).submit();
            } else {
                alert("Sorry, Input the valide month");
                return false;
            }
        } else {
            alert("Sorry, Input valid credit card number");
            return false;
        } 
    }
</script>